(()=>{"use strict";const t=document.querySelector(".header"),e=document.querySelector(".intro__main-button");new class{constructor(t){this.elem=t,this.navigation=this.elem.querySelector(".navigation"),this.toggleNavigationButton=this.elem.querySelector(".navigation__toggle-button")}init(){this.addListeners()}addListeners(){this.toggleNavigationButton.addEventListener("click",(()=>{this.navigation.classList.toggle("navigation--mobile")}))}}(t).init(),e.addEventListener("click",(()=>{alert('You clicked on button "explore", but nothing happened! But something good will happen in your life in the future:)')}))})();